jQuery(function(){jQuery("#close-comments").on("click",function(e){e.preventDefault(),jQuery.ajax({method:"post",url:lrb_vars.rest_url+"wp/v2/posts/"+lrb_vars.post_id,data:{comment_status:"closed"},beforeSend:function(e){e.setRequestHeader("X-WP-Nonce",lrb_vars.comment_nonce)}}).done(function(e){alert("Success! Comments were closed.")})})});